(7091, 547, '_e-bulletin_title', 'field_53273990f9cab'),
(7092, 547, 'ne_title', 'FOIP IMPACTS ON MUNICIPALITIES AND OTHER PUBLIC BOARDS'),
(7093, 547, '_ne_title', 'field_531fb61d778a4'),
(7094, 547, 'ne_content', 'The FOIP Act applies to all Public Bodies, which includes municipalities. As the public becomes more aware of their rights under the FOIP Act, it becomes more important for municipalities to make sure that they understand the FOIP Act and what it requires from them.\r\n\r\nThese recent decisions provide some further guidance on the FOIP Act and how it applies to and can impact municipalities.\r\n\r\n<a href="http://localhost/brownlee-dev/wp-content/uploads/2014/03/FOIPP-Impacts-on-Municipalities.pdf">View PDF Article</a>\r\n\r\nOctober 2012<a href="http://localhost/brownlee-dev/wp-content/uploads/2014/03/FOIPP-Impacts-on-Municipalities.pdf">\r\n</a>'),
(7095, 547, '_ne_content', 'field_531fb641778a5'),
